screen.applet:
from enigma import getDesktop, eSize, ePoint desktop_w = getDesktop(0).size().width() desktop_h = getDesktop(0).size().height() listcount = 0 iconsize = 63 itemheight = 38 padding = 45 minheight = iconsize maxwidth = desktop_w - 120 minwidth = desktop_w // 2 textwidth = self["text"].instance.size().width() width_all = textwidth + iconsize + padding
if width_all > maxwidth: textwidth = maxwidth - iconsize - (padding * 2)
if width_all < minwidth: textwidth = minwidth - iconsize - (padding * 2) listwidth = textwidth
if not self["text"].text: textsize = (textwidth, 0) else: textsizeoriginal = self["text"].getSize() textsize = (textwidth, textsizeoriginal[1]) minheight = max(iconsize, textsize[1])
if self["list"] and self.list is not None: listcount = len(self.list) minheight = max(iconsize, textsize[1]) + (itemheight * listcount)
listsize = (textwidth, itemheight * listcount)
self["text"].instance.resize(eSize(*textsize)) self["list"].instance.resize(eSize(*listsize)) self["text"].instance.move(ePoint(iconsize + padding, 0)) self["list"].instance.move(ePoint(iconsize + padding, textsize[1]+60))
wsizex = listwidth + iconsize + (padding * 2) wsizey = textsize[1] + listsize[1] + 90 if wsizey < minheight: wsizey = minheight wsize = (wsizex, wsizey) self.instance.resize(eSize(*wsize)) self.instance.move(ePoint((desktop_w - wsizex) // 2, (desktop_h - wsizey) // 2))

